import React, { Component } from 'react'
import { Table, Modal, Select, Button } from 'antd'
const { Option } = Select

class FormWaiting extends Component {
    constructor() {
        super()
        this.state = {
            carId: "",
            modalVisibel: false,
            stat: [],
            dataTable: [],
            column: [{
                title: "ผู้ติดต่อ",
                children: [{
                    title: "ลำดับที่",
                    dataIndex: "rown"
                },
                {
                    title: "ชื่อ",
                    dataIndex: "custommer_name"
                },
                {
                    title: "เบอร์ติดต่อ",
                    dataIndex: "custommer_phone"
                },
                {
                    title: "E-Mail",
                    dataIndex: "custommer_email"
                }]
            }, {
                title: "สถานะสินค้า",
                children: [{
                    title: "linkรถ",
                    dataIndex: "car_product_id"
                },
                {
                    title: "สถานะปัจจุบัน",
                    dataIndex: "car_status_name"
                },
                {
                    title: "แก้ไข",
                    dataIndex: "car_product_id",
                    render: (car_product_id) => (<div>
                        <Button onClick={() => this.ChangeStat(car_product_id)}>แก้ไขสถานะ</Button>
                    </div>)
                }]
            }]
        }
    }
    componentDidMount() {
        fetch("http://183.88.219.85:9091/api/show_stat.php")
            .then(stat => stat.json())
            .then(stat => {
                this.setState({
                    stat: stat
                })
            })
        fetch("http://183.88.219.85:9091/api/waiting_car.php")
            .then(res => res.json())
            .then(res => {
                this.setState({
                    dataTable: res
                })
            })
    }

    ChangeStat = (e) => {
        console.log(e)
        this.setState({
            modalVisibel: true,
            carId: e
        })
    }

    modalCancel = () => {
        this.setState({
            modalVisibel: false
        })
    }

    selectStat = () => {

    }
    render() {
        return (<div>
            formwaiting
            <Table bordered columns={this.state.column} dataSource={this.state.dataTable} />
            <Modal
                onCancel={this.modalCancel}
                visible={this.state.modalVisibel}>
                <div><h3>คุณ</h3></div>
                <Select style={{ width: 200 }} onSelect={this.selectStat} >
                    {this.state.stat.map(a => (
                        <Option value={a.car_status_id}>{a.car_status_name}</Option>
                    ))}
                </Select>
            </Modal>
        </div>)
    }
}

export default FormWaiting
