import React, { Component } from 'react'
import Nav from './Nav'
import ImageGallery from 'react-image-gallery'
import { Row, Col } from 'antd'


const images = [
    {
        original: './api/img/1.jpg',
        thumbnail: './api/img/1.jpg'
    },
    {
        original: './api/img/2.jpg',
        thumbnail: './api/img/2.jpg'
    },
    {
        original: './api/img/3.jpg',
        thumbnail: './api/img/3.jpg'
    },

]
class CarProfile extends Component {
    constructor() {
        super();
        this.state = {
            images: [],
            profile: []
        }
    }
    componentDidMount() {
        let id = window.location.search.split("?id=")[1],
            profiles = "profiles",
            img = "img"
        fetch("http://183.88.219.85:9091/api/profile.php", {
            method: "POST",
            body: JSON.stringify({
                type: profiles,
                id: id
            })
        }).then(res => res.json())
            .then(res => {
                this.setState({
                    profile: res
                })
            })
        fetch("http://183.88.219.85:9091/api/profile.php", {
            method: "POST",
            body: JSON.stringify({
                type: img,
                id: id
            })
        }).then(res => res.json())
            .then(res => {
                this.setState({
                    images: res
                })
            })
    }
    render() {
        return (<div>
            <Nav />
            car
            <Row>
                <Col lg={{ span: 10 }}>
                    <ImageGallery items={this.state.images} thumbnailPosition="left" showFullscreenButton="" showPlayButton="" />
                </Col>
            </Row>
        </div>)
    }
}

export default CarProfile
